import React, { useEffect, useState } from 'react';
import { Button, Container, Header, Icon } from 'semantic-ui-react';
import PropTypes from 'prop-types';
import { createMedia } from '@artsy/fresnel';
import axios from 'axios';
import MainModal from '../components/Mainmodal';

const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
});

const HomepageHeading = ({ mobile }) => {
  const [open, setOpen] = useState(false);
  const [newData, setNewData] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:3000/doctors')
      .then((response) => {
        const transformedData = response.data.map((doctor) => ({
          key: doctor.Name,
          text: doctor.Speciality,
          value: doctor.District,
          location: doctor.Location,
          image: { avatar: true, src: require('../assets/docIcon.jpg') },
        }));
        setNewData(transformedData);
      })
      .catch((error) => {
        console.error('Error fetching doctors:', error);
      });
  }, []);

  return (
    <Container text>
      {/* Background image */}
      <div
        className="background-image"
        style={{
          backgroundImage: `url('../assets/docBg.jpg')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          minHeight: '500px', // Adjust as needed
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          marginBottom: '2em', // Adjust as needed
        }}
      >
        <div style={{ textAlign: 'center', color: 'white' }}>
          <Header
            as="h1"
            content="HealthSaathi!"
            inverted
            style={{
              fontSize: mobile ? '2em' : '4em',
              fontWeight: 'normal',
              marginBottom: 0,
              marginTop: mobile ? '1.5em' : '3em',
            }}
          />
          <Header
            as="h2"
            content="Get medical attention at a moments notice"
            inverted
            style={{
              fontSize: mobile ? '1.5em' : '1.7em',
              fontWeight: 'normal',
              marginTop: mobile ? '0.5em' : '1em',
            }}
          />
          <Button onClick={() => setOpen(true)} primary size="huge">
            Get Started
            <Icon name="right arrow" />
          </Button>
          <MainModal
            doctorList={newData}
            open={open}
            setOpen={setOpen}
          />
        </div>
      </div>
    </Container>
  );
};

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
};

export default HomepageHeading;
